// ROUTER                                //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskRouter = Backbone.Router.extend({

    routes: {
        'task/add'      : 'add',
        'tasks/index'   : 'index',
        'task/:id/edit' : 'edit',
        'task/:id/view' : 'list'
    },

    // Constructor
    initialize: function (options) {

        this.tasks = options.tasks;
        this.index();

        // -- DEBUG ONLY! -----------------------------------
        this.tasks.bind('reset', this.updateDebug, this);
        this.tasks.bind('add', this.updateDebug, this);
        this.tasks.bind('remove', this.updateDebug, this);

    },


    updateDebug: function () {

        $('#output').text(JSON.stringify(this.tasks.toJSON(), null, 4));

    },


    add: function() {

        this.currentView = new APP.TaskAddView({
            tasks: this.tasks,
            task: new APP.TaskModel()
        });

        $('#primary-content').html(this.currentView.render().el);
    },


    edit: function(id) {

        var task = this.tasks.get(id);
        this.currentView = new APP.TaskEditView({task: task});
        $('#primary-content').html(this.currentView.render().el);

    },


    list: function(id) {

        var task = this.tasks.get(id);
        this.currentView = new APP.TaskListView({ task: task });
        $('#primary-content').html(this.currentView.render().el);

    },

    // Populate the html to the dom
    index: function() {

        this.currentView = new APP.TaskIndexView({ tasks: this.tasks });
        $('#primary-content').html(this.currentView.render().el);

        // we would call to the index with
        // this.tasks.fetch()
        // to pull down the index json response to populate our collection initially

    }

});

///////////////////////////////////////////
// VIEW - Add                            //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskAddView = Backbone.View.extend({

    // Fire!!!
    events: {
        'click button.save': 'save'
    },

    // Constructor
    initialize: function (options) {

        this.task  = options.task;
        this.tasks = options.tasks;
        this.task.bind('invalid', this.showErrors, this);

    },

    showErrors: function (task, errors) {

        this.$el.find('.error').removeClass('error');
        this.$el.find('.alert').html(_.values(errors).join('<br>')).show();
        // highlight the fields with errors
        _.each(_.keys(errors), _.bind(function (key) {
        this.$el.find('*[name=' + key + ']').parent().addClass('error');
        }, this));

    },

    save: function (event) {

        event.stopPropagation();
        event.preventDefault();

        // update our model with values from the form
        this.task.set({

            title: this.$el.find('input[name=title]').val(),
            author: this.$el.find('input[name=author]').val(),
            description: this.$el.find('textarea[name=description]').val(),
            // just setting random number for id would set as primary key from server
            id: Math.floor(Math.random() * 100) + 1

        });

        if (this.task.isValid()) {

            // add it to the collection
            this.tasks.add(this.task);
            // this.task.save();
            // redirect back to the index
            window.location.hash = 'tasks/index';

        }

    },

    // populate the html to the dom
    render: function () {

        this.$el.html(_.template($('#formTemplate').html(), this.task.toJSON()));
        return this;

    }
});

