// ROUTER                                //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskRouter = Backbone.Router.extend({

    routes: {
        'task/add'      : 'add',
        'tasks/index'   : 'index',
        'task/:id/edit' : 'edit',
        'task/:id/view' : 'list'
    },

    // Constructor
    initialize: function (options) {

        this.tasks = options.tasks;
        this.index();

        // -- DEBUG ONLY! -----------------------------------
        this.tasks.bind('reset', this.updateDebug, this);
        this.tasks.bind('add', this.updateDebug, this);
        this.tasks.bind('remove', this.updateDebug, this);

    },


    updateDebug: function () {

        $('#output').text(JSON.stringify(this.tasks.toJSON(), null, 4));

    },


    add: function() {

        this.currentView = new APP.TaskAddView({
            tasks: this.tasks,
            task: new APP.TaskModel()
        });

        $('#primary-content').html(this.currentView.render().el);
    },


    edit: function(id) {

        var task = this.tasks.get(id);
        this.currentView = new APP.TaskEditView({task: task});
        $('#primary-content').html(this.currentView.render().el);

    },


    list: function(id) {

        var task = this.tasks.get(id);
        this.currentView = new APP.TaskListView({ task: task });
        $('#primary-content').html(this.currentView.render().el);

    },

    // Populate the html to the dom
    index: function() {

        this.currentView = new APP.TaskIndexView({ tasks: this.tasks });
        $('#primary-content').html(this.currentView.render().el);

        // we would call to the index with
        // this.tasks.fetch()
        // to pull down the index json response to populate our collection initially

    }

});

///////////////////////////////////////////
// MODEL                                 //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskModel = Backbone.Model.extend({

    // You can set any defaults you would like here
    defaults: {
        title       : '',
        description : '',
        author      : 'Vitor Britto'
    },

    validate: function (attrs) {

        var errors = {};

        if (!attrs.title) {
            errors.title = 'Please type a title for this note';
        }
        if (!attrs.description) {
            errors.description = 'You gotta write a description too';
        }
        if (!attrs.author) {
            errors.author = 'Please type the name of author';
        }
        if (!_.isEmpty(errors)) {
            return errors;
        }

    }

});

APP.TaskCollection = Backbone.Collection.extend({

    // Reference to this collection's model
    model: APP.TaskModel

});

///////////////////////////////////////////
// VIEW - Add                            //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskAddView = Backbone.View.extend({

    // Fire!!!
    events: {
        'click button.save': 'save'
    },

    // Constructor
    initialize: function (options) {

        this.task  = options.task;
        this.tasks = options.tasks;
        this.task.bind('invalid', this.showErrors, this);

    },

    showErrors: function (task, errors) {

        this.$el.find('.error').removeClass('error');
        this.$el.find('.alert').html(_.values(errors).join('<br>')).show();
        // highlight the fields with errors
        _.each(_.keys(errors), _.bind(function (key) {
        this.$el.find('*[name=' + key + ']').parent().addClass('error');
        }, this));

    },

    save: function (event) {

        event.stopPropagation();
        event.preventDefault();

        // update our model with values from the form
        this.task.set({

            title: this.$el.find('input[name=title]').val(),
            author: this.$el.find('input[name=author]').val(),
            description: this.$el.find('textarea[name=description]').val(),
            // just setting random number for id would set as primary key from server
            id: Math.floor(Math.random() * 100) + 1

        });

        if (this.task.isValid()) {

            // add it to the collection
            this.tasks.add(this.task);
            // this.task.save();
            // redirect back to the index
            window.location.hash = 'tasks/index';

        }

    },

    // populate the html to the dom
    render: function () {

        this.$el.html(_.template($('#formTemplate').html(), this.task.toJSON()));
        return this;

    }
});

///////////////////////////////////////////
// VIEW - Edit                           //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskEditView = Backbone.View.extend({

    // Events Init
    events: {
        'click button.save': 'save'
    },

    // Constructor
    initialize: function (options) {
        this.task  = options.task;
    },

    save: function (event) {

        // This keeps the form from submitting
        event.stopPropagation();
        event.preventDefault();

        // Update our model with values from the form
        this.task.set({
            title: this.$el.find('input[name=title]').val(),
            author: this.$el.find('input[name=author]').val(),
            description: this.$el.find('textarea[name=description]').val()
        });

        // We would save to the server here with this.task.save();
        // Redirect back to the index
        window.location.hash = 'tasks/index';

    },

    // Populate the html to the dom
    render: function () {
        this.$el.html(_.template($('#formTemplate').html(), this.task.toJSON()));
        return this;
    }

});

///////////////////////////////////////////
// VIEW - Index                          //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskIndexView = Backbone.View.extend({

    // Constructor
    initialize: function (options) {

        // model is passed through
        this.tasks = options.tasks;
        this.tasks.bind('reset', this.addAll, this);

    },

    // Populate the html to the dom
    render: function () {

        this.$el.html($('#indexTemplate').html());
        this.addAll();
        return this;

    },

    addAll: function () {

        // Clear out the container each time you render index
        this.$el.find('tbody').children().remove();
        _.each(this.tasks.models, $.proxy(this, 'addOne'));

    },

    addOne: function (task) {

        var view = new APP.TaskRowView({
            tasks: this.tasks,
            task: task
        });

        this.$el.find('tbody').append(view.render().el);

    }

});


///////////////////////////////////////////
// VIEW - List                           //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskListView = Backbone.View.extend({

    // Constructor
    initialize: function (options) {
        this.task = options.task;
    },

    // Populate the html to the dom
    render: function () {
        this.$el.html(_.template($('#listTemplate').html(), this.task.toJSON()));
        return this;
    }
});


///////////////////////////////////////////
// VIEW - Row                            //
///////////////////////////////////////////

'use strict';

var APP = window.APP || {};

APP.TaskRowView = Backbone.View.extend({

    // the wrapper defaults to div, so only need to set this if you want something else
    // like in this case we are in a table so a tr
    tagName: 'tr',

    // Fire!!!
    events: {
        'click a.delete': 'destroy'
    },

    // the constructor
    initialize: function (options) {

        // model is passed through
        this.task  = options.task;
        this.tasks = options.tasks;

    },

    // populate the html to the dom
    render: function () {

        this.$el.html(_.template($('#rowTemplate').html(), this.task.toJSON()));
        return this;

    },

    // delete the model
    destroy: function (event) {

        event.preventDefault();
        event.stopPropagation();

        // we would call
        // this.model.destroy();
        // which would make a DELETE call to the server with the id of the item
        this.tasks.remove(this.task);
        this.$el.remove();

    }

});

